postUpdate.source:
logger.warn(">>> SCRIPT MODIFY USER: " + object.name + " <<<");
logger.warn(">>> SCRIPT MODIFY USER: " + object._id + " <<<");

let mail = object.mail;
let validMail = false;

if (/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/.test(mail)){
  logger.warn(">>> SCRIPT MODIFY USER: EMAIL IS VALID <<<");
  validMail = true;
} else {
  logger.warn(">>> SCRIPT MODIFY USER: EMAIL IS NOT VALID <<<");
  validMail = false;
}

openidm.patch("managed/user/" + object._id, null, [{"operation":"replace", "field":"validMail", "value":validMail}]);
